import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';

import { faUser, faEnvelope, faUserTie, faPhone, faLock } from '@fortawesome/free-solid-svg-icons';

import * as CryptoJS from 'crypto-js';

import { Router } from '@angular/router';
import { codeValidate } from './codeValidate';

@Component({
  selector: 'app-signup',
  templateUrl: './signup.component.html',
  styleUrls: ['./signup.component.css']
})
export class SignupComponent implements OnInit {

  title  = 'localStorage';

  Signupform : FormGroup;

  faUser = faUser;

  faEnvelope = faEnvelope;

  faUserTie = faUserTie;

  faPhone = faPhone;

  faLock = faLock;

  user : any = {};


  // Signupform = new FormGroup({

  //   email : new FormControl("",{ validators : [Validators.required, Validators.email] }),

  //   password : new FormControl("",{ validators : [Validators.required, Validators.minLength(8)] })

  // });


  constructor( private router : Router) {

    this.Signupform = new FormGroup({

      fname : new FormControl('',[Validators.required, Validators.minLength(2),Validators.pattern('[a-zA-Z].*')]),

      lname : new FormControl('',[Validators.required, Validators.minLength(1),Validators.pattern('[a-zA-Z].*')]),

      // username : new FormControl('',[Validators.required, Validators.minLength(4),Validators.pattern('[a-zA-Z].*')]),

      email : new FormControl('',[Validators.required,Validators.email]),

      number : new FormControl('', [Validators.required, Validators.pattern("^((\\+91-?)|0)?[0-9]{10}$")]),

      gender : new FormControl('',[Validators.required]),

      password : new FormControl('',[Validators.required, Validators.minLength(6)]),

      confirmpassword : new FormControl('',[Validators.required, Validators.minLength(6)]),

      image : new FormControl(),

      docImage : new FormControl()

    },

    { validators: [codeValidate.passwordMatching , /* codeValidate.checkUsernameTaken , */ codeValidate.emailMatch] },


    
    )

    this.Signupform.patchValue({


      image : "\"data:image/png;base64,\"",

      docImage : "\"data:image/png;base64,\""
    })

  }

  ngOnInit(): void {

  }

  get fname(){

    return this.Signupform.get('fname');
  }

  get lname(){

    return this.Signupform.get('lname');
  }

  // get username(){

  //   return this.Signupform.get('username');
  // }

  get email(){

    return this.Signupform.get('email');
  }

  get number(){

    return this.Signupform.get('number');
  }

  get password(){

    return this.Signupform.get('password');
  }

  get confirmpassword(){

    return this.Signupform.get('confirmpassword');
  }

  onSubmit(){

    this.user = Object.assign(this.user,this.Signupform.value);

    // alert(JSON.stringify(this.user))

    const secret = "y@QU+fWq8/3**#o";

    let x = CryptoJS.AES.encrypt(this.user.password,secret).toString();

    this.user.password = x;




    // let y = CryptoJS.AES.decrypt(this.user.password,secret)

    // alert(y.toString(CryptoJS.enc.Utf8));



    // alert(JSON.stringify(this.user.password))

    // localStorage.setItem('formdata', JSON.stringify(this.Signupform.value));



    this.addUserToDatabase(this.user);

    this.Signupform.reset();

    this.router.navigate(['login']);
  }

  addUserToDatabase(user : any){

    let users = [];

    let admin = [];

    admin.push('anuradha_rai@persistent.com','anuradha@gmail.com')

    if(localStorage.getItem('Users')){

      users = JSON.parse(localStorage.getItem('Users') || '{}');

      users = [...users,user];
    
    }
    else{

      users = [user];
    }

    localStorage.setItem('Users',JSON.stringify(users));

    localStorage.setItem('Admins', JSON.stringify(admin));

  }
  
  
}
